/**
 * ////////////////////////////////////////////////////
 * Telegram Bot API sender
 * Subflow per Node-Red
 * node-telegram-bot-api-sender
 * ////////////////////////////////////////////////////
 * Author: Bruno Leonardi <b.leonardi78@gmail.com>
 * ////////////////////////////////////////////////////
 */

////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////

//codice stato risposta
var status = msg.statusCode || 0

//messaggio di errore
var error_message = null

////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////

//verifico se esiste il payload
if (msg.payload) {
    try {
        //lo converto in oggetto
        msg.payload = (typeof msg.payload === 'string') ? JSON.parse(msg.payload) : msg.payload
    } catch (error) {
        status = 0
        msg.payload = {
            ok: false,
            error_code: 0,
            description: "Error JSONParse convertion"
        }
    }

    //controllo se è una risposta di telegram
    //payload: "{"ok":true,"result":[]}"
    if (msg.payload.ok === undefined) {
        status = 0
        msg.payload = {
            ok: false,
            error_code: 0,
            description: "It is not a Telegram message"
        }
    }
} else {
    status = 0
    msg.payload = {
        ok: false,
        error_code: 0,
        description: "Payload message not exists"
    }
}

////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////

//invio esito
if (status == 200) {
    node.status({ fill: "blue", shape: "dot", text: "Response Ok" })
    node.status({ fill: "green", shape: "ring", text: "Ready" })
    return { ok: true, status: status, request: msg.url, response: msg.payload.result }
} else {
    //https://core.telegram.org/api/errors
    if (msg.payload) {
        error_message = msg.payload.description || "Unknown error"
    } else {
        error_message = "Unknown error"
    }

    error_message = `${status} | ${error_message}`
    error_message = new Error(error_message)
    node.status({ fill: "red", shape: "dot", text: error_message.message })
    node.error(error_message, msg)
    return { ok: false, status: status, request: msg.url, response: error_message }
}

