/**
 * Telegram Bot Api Javascript for Node-RED
 */
const TelegramBotApi = global.get('TelegramBotApi.class')
const Bot = new TelegramBotApi(msg)

/**
 * Tools Class Collections Javascript for Node-RED
 */
const Tools = global.get('Tools.class')
const js = new Tools(msg)

////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////

/**
 * ////////////////////////////////////////////////////
 * Telegram Bot API sender
 * Subflow per Node-Red
 * node-telegram-bot-api-sender
 * ////////////////////////////////////////////////////
 * Author: Bruno Leonardi <b.leonardi78@gmail.com>
 * ////////////////////////////////////////////////////
 */

////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////

const global_save = flow.get('global_save')

////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////

/**
 * Aggiorna i dati global del messaggio inviato
 */
function SetGlobalChat() {
    //imposto url
    var url = msg.request
    if (!url) return
    url = url.replace(Bot.url_base, '')

    //imposto id della chat
    const chat_id = Bot.GetChatId(url)
    if (!chat_id) return

    //eliminazione messaggio
    if (js.TextExists(url, `deleteMessage\?`, 'i')) {
        Bot.ResetGlobalChat(chat_id)
        Bot.SetGlobalMessageId(chat_id, msg.response.message_id)
        Bot.SetGlobalDate(chat_id)
        Bot.SetGlobalPending(chat_id)
        Bot.SetGlobalType(chat_id, 'delete')
        Bot.SetGlobalKeycode(chat_id)
        return
    }

    if (!global_save) return

    //memorizzo le info
    Bot.SetGlobalMessageId(chat_id, msg.response.message_id)
    Bot.SetGlobalDate(chat_id)
    Bot.SetGlobalPending(chat_id)
    Bot.SetGlobalType(chat_id)

    if (js.TextExists(url, `&reply_markup=`)) {
        Bot.SetGlobalPending(chat_id, true)
        var keyboard = js.JSO(url.substr(url.indexOf('&reply_markup=') + 14, url.lastIndexOf('}')))
        if (js.TextExists(url, `"keyboard":`)) {
            if (js.TextExists(url, `"request_contact":true`)) {
                Bot.SetGlobalType(chat_id, 'request_contact')
            } else if (js.TextExists(url, `"request_location":true`)) {
                Bot.SetGlobalType(chat_id, 'request_location')
            } else {
                Bot.SetGlobalType(chat_id, 'keyboard')
            }
            Bot.SetGlobalData(chat_id, Bot.GetKeyboardButtons(keyboard.keyboard))
        } else if (js.TextExists(url, `"inline_keyboard":`)) {
            Bot.SetGlobalType(chat_id, 'inline_keyboard')
            Bot.SetGlobalData(chat_id, Bot.GetKeyboardButtons(keyboard.inline_keyboard))
        } else {
            Bot.SetGlobalPending(chat_id)
            Bot.SetGlobalKeycode(chat_id)
        }
    } else {
        Bot.SetGlobalKeycode(chat_id)
    }

    return
}

////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////

try {
    if (msg.ok == true && msg.status == 200 && msg.request && msg.response) {
        SetGlobalChat()
    }
} catch (error) {
    node.error(error, msg)
}

return msg

if (msg.telegram) {
    var message = { telegram: RED.util.cloneMessage(msg.telegram) }
    return [RED.util.cloneMessage(msg), message]
} else {
    return [RED.util.cloneMessage(msg), msg]
}

/**
 * Telegram Bot Api Javascript for Node-RED
 */
const TelegramBotApi = global.get('TelegramBotApi.class')
const Bot = new TelegramBotApi(msg)

////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////

if (msg.url && typeof msg.url === 'string' && msg.url.includes('deleteMessage')) {
    return [msg, null, null]
} else if (msg.forward && msg.forward.method && msg.forward.method == 'deleteMessage') {
    return [msg, null, null]
}

////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////

var url = global.get('telegram.url_base')
var action = Bot.GetAction()
var chat_id = null
msg.original_message = RED.util.cloneMessage(msg)

if (env.get("send_action")) {
    try {
        if (msg.url) {
            chat_id = Bot.GetChatId(msg.url)
            if (!chat_id) return [msg.original_message, null]
            action = Bot.GetAction(msg.url)
            msg.method = 'POST'
            msg.url = `${url}sendChatAction?chat_id=${chat_id}&action=${action}`
            return [null, msg]
        } else if (msg.forward) {
            chat_id = msg.forward.chat_id
            if (!chat_id) return [msg.original_message, null]
            action = Bot.GetAction(msg.forward.method)
            msg.method = 'POST'
            msg.url = `${url}sendChatAction?chat_id=${chat_id}&action=${action}`
            return [null, msg]
        } else if (msg.telegram) {
            chat_id = msg.telegram.chat_id
            if (!chat_id) return [msg.original_message, null]
            action = Bot.GetAction(msg.telegram.content_type)
            msg.method = 'POST'
            msg.url = `${url}sendChatAction?chat_id=${chat_id}&action=${action}`
            return [null, msg]
        }
    } catch (error) {
        node.error(error, msg)
        return [msg.original_message, null]
    }
}

delete msg.original_message
return [msg, null]

/**
 * Telegram Bot Api Javascript for Node-RED
 */
const TelegramBotApi = global.get('TelegramBotApi.class')
const Bot = new TelegramBotApi(msg)

/**
 * Tools Class Collections Javascript for Node-RED
 */
const Tools = global.get('Tools.class')
const js = new Tools(msg)

////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////

//https://core.telegram.org/bots/api#botcommandscope
const BOT_COMMAND_SCOPE = Bot.BOT_COMMAND_SCOPE

//metodo
var COMMAND_METHOD = env.get("command_method")

//scopo
var COMMAND_SCOPE = env.get("command_scope")

//linguaggio
var COMMAND_LANGUAGE = env.get("language_code")

//comandi array o string
var COMMANDS = env.get("commands")

////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////

//update status node
if (msg.payload == "reset_status_node") {
    if (COMMAND_METHOD == 'setMyCommands') {
        node.status({ fill: "grey", shape: "ring", text: "setMyCommands " + COMMAND_SCOPE })
    } else if (COMMAND_METHOD == 'deleteMyCommands') {
        node.status({ fill: "grey", shape: "ring", text: "deleteMyCommands " + COMMAND_SCOPE })
    } else if (COMMAND_METHOD == 'getMyCommands') {
        node.status({ fill: "grey", shape: "ring", text: "getMyCommands " + COMMAND_SCOPE })
    }
    delete msg.payload
    return null
}

////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////

//funziona solo con messaggi telegram
if (msg.telegram === undefined) {
    node.status({ fill: "yellow", shape: "dot", text: "Telegram message not found" })
    return null
}

////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////

if (msg.botcommands) {
    COMMAND_METHOD = msg.botcommands.method || COMMAND_METHOD
    COMMAND_SCOPE = msg.botcommands.scope || COMMAND_SCOPE
    COMMANDS = js.JSS(msg.botcommands.commands) || COMMANDS
    delete msg.botcommands
} else if (js.IsStringFull(COMMANDS)) {
    COMMANDS = COMMANDS.trim()
    let global_object = js.GetGlobalVariable(COMMANDS)
    COMMANDS = (global_object) ? js.JSS(global_object) : null
} else {
    COMMANDS = js.JSS(COMMANDS)
}

if (js.IsStringEmpty(COMMANDS)) COMMANDS = null

////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////

if (!COMMANDS && COMMAND_METHOD == 'setMyCommands') {
    node.status({ fill: "red", shape: "dot", text: "Commands not found" })
    return null
}

////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////

var forward = {
    method: COMMAND_METHOD,
    commands: COMMANDS,
    options: {
        scope: js.Template(BOT_COMMAND_SCOPE[COMMAND_SCOPE]),
        language_code: COMMAND_LANGUAGE
    }
}

////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////

node.status({ fill: "green", shape: "ring", text: COMMAND_METHOD + ' ' + COMMAND_SCOPE + ' ' + COMMAND_LANGUAGE})

////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////

msg.global_save = false
msg.forward = forward
return msg

