const COMMAND_METHOD = env.get("command_method")

//scopo
const COMMAND_SCOPE = env.get("command_scope")

//comandi array o string
var COMMANDS = env.get("commands")

////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////

//validazione dei comandi
if (typeof COMMANDS === 'string') {
    try {
        let global_commands = global.get(COMMANDS)
        if (!global_commands && !Array.isArray(global_commands)) {
            node.status({ fill: "red", shape: "dot", text: "The global variable is not a valid array" })
        }
    } catch (error) {
        node.status({ fill: "red", shape: "dot", text: "The global variable not exists" })
    }
} else if (typeof COMMANDS === 'object' && !Array.isArray(COMMANDS)) {
    node.status({ fill: "red", shape: "dot", text: "The object is not a valid array" })
}

////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////

////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////

//update status node
if (COMMAND_METHOD == 'setMyCommands') {
    node.status({ fill: "grey", shape: "ring", text: "setMyCommands " + COMMAND_SCOPE })
} else if (COMMAND_METHOD == 'deleteMyCommands') {
    node.status({ fill: "grey", shape: "ring", text: "deleteMyCommands " + COMMAND_SCOPE })
} else if (COMMAND_METHOD == 'getMyCommands') {
    node.status({ fill: "grey", shape: "ring", text: "getMyCommands " + COMMAND_SCOPE })
}

